import API from "../utils/API";
import React from "react";
import { SaveButton } from "./buttons";
import {
  Container,
  Header,
  Names,
  Buttons,
  LargeFont,
  MedFont
} from "./playList_css";

class Books extends React.Component {
  state = {
    artist: [],
    title: ""
  };

  componentDidMount() {
    this.getPlaylist();
  }

  static getDerivedStateFromProps(props, state) {
    if (props.appData.dbLoad) {
      this.getPlaylist();
      props.changeState({ dbLoad: 0 });
    }
    if (props.appData.scrape) {
      this.scrape();
      props.changeState({ scrape: 0 });
    }
  }
  componentWillReceiveProps = ({ appData }) => {
    if (appData.scrape) {
      // only scrapes
      this.scrape();
      this.setScrape({ scrape: 0 });
    } else {
      this.setState({ artist: appData.savedTunes });
    }
  };

  scrape = () => {
    API.scrape()
      .then(res => {
        this.addScrapeToDb(res["data"]);
      })
      .catch(err => console.log(err));
  };

  addScrapeToDb = data => {
    API.addScrape(data).then(res => {
      console.log(res);
      this.getPlaylist();
    });
  };

  getPlaylist = () => {
    API.getPlaylist()
      .then(res => {
        this.setState({
          artist: res["data"]
        });
      })
      .catch(err => {
        console.log(err);
        this.getPlaylist();
      });
  };

  random = () => {
    var random = Math.floor(Math.random() * 100000);

    return random;
  };

  saveTunes = e => {
    // check if user is logged in
    if (this.props.appData.login && this.props.appData.id) {
      e.id = this.props.appData.id;
      API.saveTune(e).then(res => {
        this.props.changeState({ message: res.data, db: 1 });
      });
    } else {
      this.props.changeState({ message: "you'll need to login" });
    }
  };

  deleteTunes = e => {
    API.deleteTunes(e).then(res => {
      this.props.changeState({ message: res.data });
    });
  };

  playTunes = e => {
    API.playTunes(e)
      .then(res => {
        console.log(res);
        this.props.changeState({ playerID: res.data });
      })
      .catch(err => {
        console.log(err);
        return err;
      });
  };

  render() {
    // if state.login = false, return(Container with login)
    return (
      <Container>
        {this.state["artist"].map(e => (
          <Header key={this.random()}>
            <Names>
              <LargeFont>{e.artist}</LargeFont>
              <MedFont> {e.title} </MedFont>
            </Names>
            <Buttons>
              <img src={e.source} />
              <button
                onClick={() => this.playTunes(e)}
                style={{ margin: "3%" }}
              >
                {" "}
                Play{" "}
              </button>
              {this.props.appData.scrape ? (
                <button
                  onClick={() => this.saveTunes(e)}
                  style={{ margin: "3%" }}
                >
                  Save
                </button>
              ) : (
                <button
                  onClick={() => this.deleteTunes(e)}
                  style={{ margin: "3%" }}
                >
                  {" "}
                  Delete{" "}
                </button>
              )}
            </Buttons>
          </Header>
        ))}
      </Container>
    );
  }
}

export default Books;
