import React from "react";
import { Wrapper, Col1, Col2 } from "./container_css";
import PlayList from "./playList";
import Login from "./login";
import API from "../utils/API";
import Player from "./player";

class Container extends React.Component {
  // only fires at first load

  componentDidMount() {
    this.loggedIn();
  }

  loggedIn = () => {
    API.isLogged()
      .then(res => {
        if (res.data.password) {
          this.props.changeState({
            login: true
          });
        } else {
          this.props.changeState({
            login: false
          });
        }
      })
      .catch(err => console.log(err));
  };

  // props - appData (state) changeState (changeState)
  render() {
    // https://medium.freecodecamp.org/the-basics-of-destructuring-props-in-react-a196696f5477
    const {
      appData: { playerID, message, scrape, login }
    } = this.props;
    return (
      <Wrapper>
        <Col1>
          <Player playerID={playerID} message={message} />
        </Col1>
        <Col2>
          {login ? (
            <Login
              changeState={this.props.changeState}
              appState={this.props.appData}
            />
          ) : (
            <PlayList
              changeState={this.props.changeState}
              appData={this.props.appData}
            />
          )}
        </Col2>
      </Wrapper>
    );
  }
}

export default Container;
