import API from "../utils/API";
import React from "react";
import { SaveButton } from "./buttons";
import {
  Container,
  Header,
  Names,
  Buttons,
  LargeFont,
  MedFont
} from "./playList_css";

class Books extends React.Component {
  state = {
    tunesShow: []
  };

  componentDidMount() {
    var show;
    if (this.props.appData.allTunesShow) {
      show = this.props.appData.allTunes;
    } else {
      show = this.props.appData.userTunes;
    }
    this.setState({ tunesShow: show });
  }

  componentWillReceiveProps = ({ appData, changeState }) => {
    if (appData.scrape) {
      // only scrapes
      this.scrape();
      changeState({ scrape: 0 });
    }
    // } else {
    //   this.setState({ artist: appData.savedTunes });
    // }
    // if (appData.allTunesShow) {
    //   this.getPlaylist();
    // }
  };

  scrape = () => {
    API.scrape()
      .then(res => {
        this.addScrapeToDb(res["data"]);
      })
      .catch(err => console.log(err));
  };

  addScrapeToDb = data => {
    API.addScrape(data).then(res => {
      console.log(res);
      this.getPlaylist();
    });
  };

  getPlaylist = () => {
    API.getPlaylist()
      .then(res => {
        console.log(res.data);

        this.props.changeState({ allTunes: res["data"] });
      })
      .catch(err => {
        console.log(err);
        this.getPlaylist();
      });
  };

  random = () => {
    var random = Math.floor(Math.random() * 100000);

    return random;
  };

  saveTunes = e => {
    // check if user is logged in
    if (this.props.appData.login && this.props.appData.id) {
      e.id = this.props.appData.id;
      API.saveTune(e).then(res => {
        this.props.changeState({ message: res.data });
      });
    } else {
      this.props.changeState({ message: "you'll need to login" });
    }
  };

  deleteTunes = e => {
    API.deleteTunes(e).then(res => {
      this.props.changeState({ message: res.data });
    });
  };

  playTunes = e => {
    API.playTunes(e)
      .then(res => {
        this.props.changeState({ playerID: res.data });
      })
      .catch(err => {
        console.log(err);
        return err;
      });
  };

  render() {
    const {
      appData: { savedTunes, scrape }
    } = this.props;
    console.log(savedTunes);
    return (
      <Container>
        {savedTunes.map(e => (
          <Header key={this.random()}>
            <Names>
              <LargeFont>{e.artist}</LargeFont>
              <MedFont> {e.title} </MedFont>
            </Names>
            <Buttons>
              <img alt="meaningful" src={e.source} />
              <button
                onClick={() => this.playTunes(e)}
                style={{ margin: "3%" }}
              >
                Play
              </button>
              {scrape ? (
                <button
                  onClick={() => this.saveTunes(e)}
                  style={{ margin: "3%" }}
                >
                  Save
                </button>
              ) : (
                <button
                  onClick={() => this.deleteTunes(e)}
                  style={{ margin: "3%" }}
                >
                  {" "}
                  Delete{" "}
                </button>
              )}
            </Buttons>
          </Header>
        ))}
      </Container>
    );
  }
}

export default Books;
