import React, { Component } from "react";
import API from "../utils/API";
// import { Nav } from "semantic-ui-react";

import styled from "styled-components";

// props: logOut, changeScrape,

const Wrapper = styled.div`
display: flex
justify-content: center;

`;
const HeaderWrapper = styled.div`
  margin: 3% 5% 0 5%;
  border-radius: 4%;
  border: 1px solid yellow;
  width: 85%;
`;
const Header = styled.div`
  // margin: 2% 5% 0 5%;
  padding: 3%;
`;
const Body = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;

  margin: 0 5% 2% 5%;
`;

const Button = styled.div`
  border: 1px solid black;
  padding: 0.5%;
  border-radius: 10%;
`;

export default class Nav extends Component {
  refreshTunes = ({ changeState }) => {
    var message = "the newest scrape";
    changeState({ scrape: true, message: message });
  };

  dbLoad = ({ changeState }) => {
    changeState({ dbLoad: 1 });
  };

  getSavedTunes = ({ appData, changeState }) => {
    this.loggedIn();
  };

  loggedIn = () => {
    API.isLogged()
      .then(res => {
        if (res.data.password) {
          this.getTunes(res);
        } else {
          this.props.changeState({
            login: false,
            logInShow: 1
          });
        }
      })
      .catch(err => console.log(err));
  };

  getTunes = ({ data }) => {
    // console.log(data);
    API.getSavedTunes(data._id).then(res => {
      console.log(res);
      this.props.changeState({
        login: true,
        savedTunes: res.data["data"]
      });
    });
  };

  // getAllTunes = () => {
  //   API.getPlayList:
  // }

  handleLogOut = () => {
    API.logout().then(res => {
      this.props.changeState({
        message: res.data.message,
        login: false,
        id: 0
      });
    });
  };

  render() {
    return (
      <Wrapper>
        <HeaderWrapper>
          <Header>
            <h1 style={{ fontSize: "250%" }}>Good Tunes</h1>
            <h2> recommended tunes from the internet </h2>
          </Header>

          <Body>
            <Button onClick={() => this.refreshTunes(this.props)}>
              Scrape New Tunes
            </Button>

            <Button onClick={() => this.getTunes(this.props)}>
              See Db Tunes
            </Button>

            <Button onClick={() => this.getSavedTunes(this.props)}>
              Saved PlayList
            </Button>

            <Button onClick={this.handleLogOut}>LogOut</Button>
          </Body>
        </HeaderWrapper>
      </Wrapper>
    );
  }
}
